export function VoiceRecorder({ onStop }) {
  const [isRecording, setIsRecording] = React.useState(false);
  const mediaRecorder = React.useRef(null);

  const start = async () => {
    const permission = await Audio.requestPermissionsAsync();
    if (!permission.granted) return;
    const recorder = new Audio.Recording();
    await recorder.prepareToRecordAsync(Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY);
    await recorder.startAsync();
    mediaRecorder.current = recorder;
    setIsRecording(true);
  };

  const stop = async () => {
    await mediaRecorder.current.stopAndUnloadAsync();
    const uri = mediaRecorder.current.getURI();
    setIsRecording(false);
    onStop(uri);
  };

  return (
    <Pressable onPress={isRecording ? stop : start} style={{ padding: 16, backgroundColor: isRecording ? '#c62828' : '#2962ff' }}>
      <Text style={{ color: 'white' }}>{isRecording ? 'Stop' : 'Start'} recording</Text>
    </Pressable>
  );
}
