import React from 'react';
import { Pressable, View, Text, Image } from 'react-native';
import { FlashList } from '@shopify/flash-list';
import { useTheme } from '@/theme';

type DebateFeedProps = {
  items: Array<{ id: string; title: string; preview: string; avatar: string }>;
  onSelect: (item: DebateFeedProps['items'][number]) => void;
};

const Avatar: React.FC<{ uri: string }> = React.memo(({ uri }) => (
  <Image source={{ uri }} style={{ width: 48, height: 48, borderRadius: 24 }} />
));

export const DebateFeed: React.FC<DebateFeedProps> = ({ items, onSelect }) => {
  const { theme } = useTheme();

  const renderItem = React.useCallback(({ item }) => (
    <Pressable onPress={() => onSelect(item)} accessibilityRole="button">
      <View style={{ flexDirection: 'row', paddingVertical: theme.spacing.md, paddingHorizontal: theme.spacing.lg }}>
