        <View style={{ marginLeft: theme.spacing.md, flex: 1 }}>
          <Text style={theme.typography.title.xs} numberOfLines={1}>{item.title}</Text>
          <Text
            style={[theme.typography.body.sm, { color: theme.colors.muted, marginTop: theme.spacing.xs }]}
            numberOfLines={2}
          >
            {item.preview}
          </Text>
        </View>
      </View>
    </Pressable>
